DELIMITER //
CREATE PROCEDURE create_give_a_gift (parm_id_gift INT,
parm_id_user_sender INT,
parm_id_user_receiver INT,
parm_quantity INT)

BEGIN
INSERT INTO gift_user (id_gift, id_user_sender, id_user_receiver, quantity)
VALUE (parm_id_gift, parm_id_user_sender, parm_id_user_receiver, parm_quantity);
update `user` set coin = coin - (select price from gift where id_gift = parm_id_gift) where id_user = parm_id_user_sender;
END //
DELIMITER ;
DELIMITER //

CREATE PROCEDURE update_give_a_gift(parm_id_gift INT,
parm_id_user_sender INT,
parm_id_user_receiver INT,
parm_quantity INT)
BEGIN
update gift_user set quantity = quantity + parm_quantity where id_gift = parm_id_gift and id_user_sender = parm_id_user_sender and id_user_receiver = parm_id_user_receiver;
update `user` set coin = coin - (select price from gift where id_gift = parm_id_gift) where id_user = parm_id_user_sender;
END //
